  return (
    <>
      <Hero>
        <Title>Welcome to NextTrip, {props.userName}!</Title>
        <Subtitle>Discover the world, one country at a time. Click on countries you've visited to mark them and start planning your next adventure.</Subtitle>
      </Hero>
      <MapContainer>
        <MapComponent />
      </MapContainer>
    </>
  );
}

export default Home;
