import React from "react";
import { ComposableMap, Geographies, Geography } from "react-simple-maps";

const geoUrl =
  "https://raw.githubusercontent.com/deldersveld/topojson/master/world-countries.json";

// List of countries the user has visited (ISO A3 codes)
const visitedCountries = ["FRA", "ISR", "USA", "JPN", "BRA"];

function Home() {
  return (
    <div style={{ padding: "2rem" }}>
      <h1>Welcome to NextTrip</h1>
      <p>
        Discover and rate your travel experiences. Get personalized travel
        recommendations!
      </p>

      <div style={{ width: "100%", height: "500px" }}>
        <ComposableMap projection="geoMercator">
          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map((geo) => {
                const isVisited = visitedCountries.includes(
                  geo.properties.ISO_A3
                );
                return (
                  <Geography
                    key={geo.rsmKey}
                    geography={geo}
                    style={{
                      default: {
                        fill: isVisited ? "#66bb6a" : "#D6D6DA",
                        outline: "none",
                      },
                      hover: {
                        fill: isVisited ? "#388e3c" : "#F53",
                        outline: "none",
                      },
                      pressed: {
                        fill: isVisited ? "#2e7d32" : "#E42",
                        outline: "none",
                      },
                    }}
                  />
                );
              })
            }
          </Geographies>
        </ComposableMap>
      </div>
      <div>
        {" "}
        <ComposableMap>
          <Geographies geography="/features.json">
            {({ geographies }) =>
              geographies.map((geo) => (
                <Geography key={geo.rsmKey} geography={geo} />
              ))
            }
          </Geographies>
        </ComposableMap>
      </div>
    </div>
  );
}

export default Home;
