import React, { useState } from 'react';
import { ComposableMap, Geographies, Geography, Marker } from "react-simple-maps";
import { geoCentroid } from "d3-geo";
import geoData from "../features.json";

const MapComponent = () => {
  const [visitedCountries, setVisitedCountries] = useState([]);
  const [selected, setSelected] = useState(null); // { geo, coordinates }

  const handleClick = (geo) => {
    if (selected?.geo?.properties?.ISO_A3 === geo.properties.ISO_A3) {
      setSelected(null); // סגור אם לוחצים שוב על אותה מדינה
    } else {
      const centroid = geoCentroid(geo); // מיקום על המפה
      setSelected({ geo, coordinates: centroid });
    }
  };
  

  const markAsVisited = () => {
    const code = selected.geo.properties.ISO_A3;
    if (!visitedCountries.includes(code)) {
      setVisitedCountries([...visitedCountries, code]);
    }
  };

  return (
    <div>
      <ComposableMap projection="geoMercator">
        <Geographies geography={geoData}>
          {({ geographies }) =>
            geographies.map((geo) => {
              const code = geo.properties.ISO_A3;
              const isVisited = visitedCountries.includes(code);

              return (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  onClick={() => handleClick(geo)}
                  style={{
                    default: {
                      fill: isVisited ? '#66bb6a' : '#D6D6DA',
                      outline: 'none',
                      cursor: 'pointer',
                    },
                    hover: {
                      fill: isVisited ? '#388e3c' : '#F53',
                      outline: 'none',
                    },
                    pressed: {
                      fill: isVisited ? '#2e7d32' : '#E42',
                      outline: 'none',
                    },
                  }}
                />
              );
            })
          }
        </Geographies>

        {/* Tooltip על המפה */}
        {selected && selected.coordinates && (
          <Marker coordinates={selected.coordinates}>
            <g transform="translate(-30, -20)">
              <rect width={80} height={30} fill="white" stroke="#333" rx={5} />
              <text x={5} y={20} fontSize={12} fill="#000">
                {selected.geo.properties.name}
              </text>
            </g>
          </Marker>
        )}
      </ComposableMap>

      {/* כפתור סימון */}
      {selected && (
        <div style={{ marginTop: '1rem' }}>
          {!visitedCountries.includes(selected.geo.properties.ISO_A3) ? (
            <button onClick={markAsVisited}>
              Mark "{selected.geo.properties.NAME}" as Visited
            </button>
          ) : (
            <p style={{ color: 'green' }}>
              You've already visited {selected.geo.properties.NAME}.
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default MapComponent;
