import React, { useState } from 'react';
import { ComposableMap, Geographies, Geography } from "react-simple-maps";
import geoData from "../features.json"; 


function Home() {
  const [visitedCountries, setVisitedCountries] = useState([]);

  const toggleVisited = (code) => {
    setVisitedCountries((prev) =>
      prev.includes(code)
        ? prev.filter((c) => c !== code) // הסר אם כבר ביקר
        : [...prev, code] // הוסף אם לא ביקר
    );
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Welcome to NextTrip</h1>
      <p>Click on countries you've visited to mark them.</p>

      <div style={{ maxWidth: '900px', margin: '2rem auto' }}>
        <ComposableMap projection="geoMercator">
          <Geographies geography={geoData}>
            {({ geographies }) =>
              geographies.map((geo) => {
                const code = geo.properties.ISO_A3;
                const isVisited = visitedCountries.includes(code);

                return (
                  <Geography
                    key={geo.rsmKey}
                    geography={geo}
                    onClick={() => toggleVisited(code)}
                    style={{
                      default: {
                        fill: isVisited ? '#66bb6a' : '#D6D6DA',
                        outline: 'none',
                        cursor: 'pointer',
                      },
                      hover: {
                        fill: isVisited ? '#388e3c' : '#F53',
                        outline: 'none',
                      },
                      pressed: {
                        fill: isVisited ? '#2e7d32' : '#E42',
                        outline: 'none',
                      },
                    }}
                  />
                );
              })
            }
          </Geographies>
        </ComposableMap>
      </div>

      <div>
        <h3>You visited:</h3>
        <ul>
          {visitedCountries.map((code) => (
            <li key={code}>{code}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Home;
