// components/MapLoading.jsx
import React from "react";
import styled, { keyframes } from "styled-components";
import { FaGlobeAmericas, FaMapMarkerAlt } from "react-icons/fa";

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const bounce = keyframes`
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-8px); }
`;

const LoaderWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 60vh;
  text-align: center;
`;

const Marker = styled.div`
  font-size: 24px;
  animation: ${bounce} 1.5s infinite;
  color: #ff9800;
  margin-bottom: -10px; /* Moves closer to the globe */
  z-index: 1;
`;

const RotatingGlobe = styled.div`
  font-size: 60px;
  animation: ${spin} 4s linear infinite;
  color: #1976d2;
`;

const LoadingText = styled.p`
  margin-top: 1.5rem;
  font-size: 1rem;
  color: #555;
`;

const MapLoading = () => (
  <LoaderWrapper>
    <Marker>
      <FaMapMarkerAlt />
    </Marker>
    <RotatingGlobe>
      <FaGlobeAmericas />
    </RotatingGlobe>
    <LoadingText>Exploring new destinations for you...</LoadingText>
  </LoaderWrapper>
);

export default MapLoading;
