import React, { useState } from 'react';
import { ComposableMap, Geographies, Geography } from "react-simple-maps";
import geoData from "../features.json";

const MapComponent = () => {
  const [visitedCountries, setVisitedCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState(null); // המדינה שנלחצה

  const handleCountryClick = (geo) => {
    setSelectedCountry(geo);
  };

  const markAsVisited = () => {
    const code = selectedCountry.properties.ISO_A3;
    if (!visitedCountries.includes(code)) {
      setVisitedCountries((prev) => [...prev, code]);
    }
  };

  return (
    <div>
      <ComposableMap projection="geoMercator">
        <Geographies geography={geoData}>
          {({ geographies }) =>
            geographies.map((geo) => {
              const code = geo.properties.ISO_A3;
              const isVisited = visitedCountries.includes(code);

              return (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  onClick={() => handleCountryClick(geo)}
                  style={{
                    default: {
                      fill: isVisited ? '#66bb6a' : '#D6D6DA',
                      outline: 'none',
                      cursor: 'pointer',
                    },
                    hover: {
                      fill: isVisited ? '#388e3c' : '#F53',
                      outline: 'none',
                    },
                    pressed: {
                      fill: isVisited ? '#2e7d32' : '#E42',
                      outline: 'none',
                    },
                  }}
                />
              );
            })
          }
        </Geographies>
      </ComposableMap>

      {/* פרטי מדינה שנבחרה */}
      {selectedCountry && (
        <div style={{ marginTop: '1rem', borderTop: '1px solid #ccc', paddingTop: '1rem' }}>
          <h3>{selectedCountry.properties.NAME}</h3>
          <p>This is a beautiful country! You can mark it as visited below.</p>
          {!visitedCountries.includes(selectedCountry.properties.ISO_A3) ? (
            <button onClick={markAsVisited}>Mark as Visited</button>
          ) : (
            <p style={{ color: 'green' }}>You've already visited this country.</p>
          )}
        </div>
      )}

      {/* רשימת מדינות שביקרת בהן */}
      <div style={{ marginTop: '2rem' }}>
        <h4>Visited Countries:</h4>
        <ul>
          {visitedCountries.map((code) => (
            <li key={code}>{code}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default MapComponent;
