import React, { useEffect, useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./Navbar";
import Home from "../pages/Home";
import MyTrips from "../pages/MyTrips";
import RateCountry from "../pages/RateCountry";
import CountryPage from "../pages/CountryPage";
import LandingPage from "../pages/LandingPage";
import Signup from "../pages/Signup";
import config from "../config";
import { useUser } from "../contexts/UserContext";
import styled, { keyframes } from "styled-components";

const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(255, 255, 255, 0.9);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  z-index: 9999;
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #ccc;
  border-top: 6px solid #1976d2;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
`;

const LoadingText = styled.div`
  margin-top: 20px;
  font-size: 1.2rem;
  color: #333;
  font-family: "Segoe UI", sans-serif;
`;

const AppRoutes = () => {
  const { user, setUser } = useUser();
  const [isAuthenticated, setIsAuthenticated] = useState(false);

useEffect(() => {
  const idToken = localStorage.getItem("id_token");
  const code = new URLSearchParams(
    window.location.search || window.location.hash.split("?")[1]
  ).get("code");

  const decodeToken = (token) => {
    try {
      const base64Url = token.split(".")[1];
      const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
      const jsonPayload = decodeURIComponent(
        atob(base64)
          .split("")
          .map((c) => "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2))
          .join("")
      );
      return JSON.parse(jsonPayload);
    } catch (err) {
      console.error("Invalid token:", err);
      return null;
    }
  };

  const fetchUserDetails = async (email) => {
    try {
      const response = await fetch(
        "https://6bmdup2xzi.execute-api.us-east-1.amazonaws.com/prod/GetUserData",
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${idToken}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ email }),
        }
      );

      if (!response.ok) throw new Error(`HTTP ${response.status}`);
      const userData = await response.json();
      setUser(userData);
      setIsAuthenticated(true);
    } catch (err) {
      console.error("Failed to fetch user details:", err);
      setIsAuthenticated(false);
      localStorage.removeItem("id_token");
      localStorage.removeItem("access_token");
    }
  };

  const exchangeCodeForTokens = async () => {
    try {
      const getTokenUrl = `${config.domain}/oauth2/token`;

      const response = await fetch(getTokenUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: new URLSearchParams({
          grant_type: "authorization_code",
          client_id: config.clientId,
          client_secret: config.clientSecret,
          redirect_uri: config.redirectUri,
          code,
        }).toString(),
      });

      const data = await response.json();

      if (data.id_token) {
        localStorage.setItem("id_token", data.id_token);
        localStorage.setItem("access_token", data.access_token);
        const decoded = decodeToken(data.id_token);
        if (decoded?.email) {
          await fetchUserDetails(decoded.email);
        }
      } else {
        throw new Error("No ID token received");
      }
    } catch (err) {
      console.error("Token exchange failed:", err);
      setIsAuthenticated(false);
    }
  };

  // âœ… Priority: use existing token if available
  if (idToken) {
    const decoded = decodeToken(idToken);
    if (decoded?.email) {
      fetchUserDetails(decoded.email);
    } else {
      // Invalid token
      localStorage.removeItem("id_token");
      localStorage.removeItem("access_token");
      setIsAuthenticated(false);
    }
  } else if (code) {
    // No token yet, but code is in URL (first login)
    exchangeCodeForTokens();
  } else {
    // No token or code
    setIsAuthenticated(false);
  }
}, []);


  if (isAuthenticated && !user?.userType) {
    return (
      <Overlay>
        <Spinner />
      </Overlay>
    );
  }

  return (
    <>
      {isAuthenticated && user?.userType && <Navbar />}
      <main style={{ minHeight: "calc(100vh - 64px)", padding: "2rem 0" }}>
        <Routes>
          {isAuthenticated ? (
            !user?.userType ? (
              <>
                <Route path="/" element={<Navigate to="/Signup" />} />
                <Route path="/signup" element={<Signup />} />
                <Route path="*" element={<Navigate to="/Signup" />} />
              </>
            ) : (
              <>
                <Route
                  path="/"
                  element={<Home userCountries={user.visitedCountries} />}
                />
                <Route path="/my-trips" element={<MyTrips />} />
                <Route path="/rate-country" element={<RateCountry />} />
                <Route path="/country/:countryName" element={<CountryPage />} />
                <Route path="*" element={<Navigate to="/" />} />
              </>
            )
          ) : (
            <>
              <Route path="/" element={<Navigate to="/LandingPage" />} />
              <Route path="/LandingPage" element={<LandingPage />} />
              <Route path="*" element={<Navigate to="/LandingPage" />} />
            </>
          )}
        </Routes>
      </main>
    </>
  );
};

export default AppRoutes;
